function stringToMap(t: string): Map<string, number> {
  const map = new Map<string, number>()
  for (let i = 0; i < t.length; i++) {
    if (map.has(t.charAt(i))) {
      map.set(t.charAt(i), map.get(t.charAt(i))! + 1)
    } else {
      map.set(t[i], 1)
    }
  }
  return map
}

function isHasAllMap(map: Map<string, number>): boolean {
  let flag = true
  map.forEach((value) => {
    if (value > 0) {
      flag = false
    }
  })
  return flag
}
// 滑动窗口
function minWindow(s: string, t: string): string {
  // 将t转化为hash表的形式
  const tempMap = stringToMap(t)
  let left = 0,
    result = [0, 100 ** 5],
    i = 0
  for (; i < s.length; i++) {
    if (tempMap.has(s.charAt(i))) {
      tempMap.set(s.charAt(i), tempMap.get(s.charAt(i))! - 1)
    }
    // 如果当前在i上全部包含了则进行左指针移动
    if (isHasAllMap(tempMap)) {
      while (tempMap.get(s.charAt(left)) === undefined || tempMap.get(s.charAt(left))! < 0) {
        if (tempMap.get(s.charAt(left))! < 0) {
          tempMap.set(s.charAt(left), tempMap.get(s.charAt(left))! + 1)
        }
        left++
      }
      // 计算长度值 走到这里说明有 === 0 出现
      if (result[1] - result[0] > i - left) {
        result = [left, i]
      }
    }
  }
  if (!isHasAllMap(tempMap)) {
    return ''
  }
  console.log(result)
  return s.substring(result[0], result[1] + 1)
}

console.log(
  minWindow(
    '',
    ''
  )
)
